import { Box, Button, SxProps, Theme } from "@mui/material";
import React, { useEffect, useCallback, SetStateAction } from "react";
import { useState } from 'react';

import { C1, D1, E1, F1, G1, A1, B1, C2, D2, E2, F2, G2, A2, B2, C3, D3, E3, F3, G3, A3, B3, C4, Db1, Eb1, Gb1, Ab1, Bb1, Db2, Eb2, Gb2, Ab2, Bb2, Db3, Eb3, Gb3, Ab3, Bb3 } from "../../resources/piano/"


const Sample = () => {
    const [C1_, C1__] = useState('#FFF'), [D1_, D1__] = useState('#FFF'), [E1_, E1__] = useState('#FFF'), [F1_, F1__] = useState('#FFF'), [G1_, G1__] = useState('#FFF'), [A1_, A1__] = useState('#FFF'), [B1_, B1__] = useState('#FFF')
    const [C2_, C2__] = useState('#FFF'), [D2_, D2__] = useState('#FFF'), [E2_, E2__] = useState('#FFF'), [F2_, F2__] = useState('#FFF'), [G2_, G2__] = useState('#FFF'), [A2_, A2__] = useState('#FFF'), [B2_, B2__] = useState('#FFF')
    const [C3_, C3__] = useState('#FFF'), [D3_, D3__] = useState('#FFF'), [E3_, E3__] = useState('#FFF'), [F3_, F3__] = useState('#FFF'), [G3_, G3__] = useState('#FFF'), [A3_, A3__] = useState('#FFF'), [B3_, B3__] = useState('#FFF')
    const [C4_, C4__] = useState('#FFF')

    const [Db1_, Db1__] = useState('#000'), [Eb1_, Eb1__] = useState('#000'), [Gb1_, Gb1__] = useState('#000'), [Ab1_, Ab1__] = useState('#000'), [Bb1_, Bb1__] = useState('#000')
    const [Db2_, Db2__] = useState('#000'), [Eb2_, Eb2__] = useState('#000'), [Gb2_, Gb2__] = useState('#000'), [Ab2_, Ab2__] = useState('#000'), [Bb2_, Bb2__] = useState('#000')
    const [Db3_, Db3__] = useState('#000'), [Eb3_, Eb3__] = useState('#000'), [Gb3_, Gb3__] = useState('#000'), [Ab3_, Ab3__] = useState('#000'), [Bb3_, Bb3__] = useState('#000')

    let piano_count = 0;

    const handleKeyDown = (event: KeyboardEvent) => {
        let sound = null;
        let keyboadClass = "";

        if (event.keyCode === 39) {
            piano_count += 1;
        }
        if (event.keyCode === 37) {
            piano_count -= 1;
        }
        if (piano_count === 0) {
            switch (event.key) {
                case "q":
                    sound = new Audio(C1); keyboadClass = "C1";
                    break
                case "2":
                    sound = new Audio(Db1); keyboadClass = "Db1";
                    break
                case "w":
                    sound = new Audio(D1); keyboadClass = "D1";
                    break
                case "3":
                    sound = new Audio(Eb1); keyboadClass = "Eb1";
                    break
                case "e":
                    sound = new Audio(E1); keyboadClass = "E1";
                    break
                case "r":
                    sound = new Audio(F1); keyboadClass = "F1";
                    break
                case "5":
                    sound = new Audio(Gb1); keyboadClass = "Gb1";
                    break
                case "t":
                    sound = new Audio(G1); keyboadClass = "G1";
                    break
                case "6":
                    sound = new Audio(Ab1); keyboadClass = "Ab1";
                    break
                case "y":
                    sound = new Audio(A1); keyboadClass = "A1";
                    break
                case "7":
                    sound = new Audio(Bb1); keyboadClass = "Bb1";
                    break
                case "u":
                    sound = new Audio(B1); keyboadClass = "B1";
                    break
                case "i":
                    sound = new Audio(C2); keyboadClass = "C2";
                    break
                case "9":
                    sound = new Audio(Db2); keyboadClass = "Db2";
                    break
                case "o":
                    sound = new Audio(D2); keyboadClass = "D2";
                    break
                case "0":
                    sound = new Audio(Eb2); keyboadClass = "Eb2";
                    break
                case "p":
                    sound = new Audio(E2); keyboadClass = "E2";
                    break
                case "@":
                    sound = new Audio(F2); keyboadClass = "F2";
                    break
                case "^":
                    sound = new Audio(Gb2); keyboadClass = "Gb2";
                    break
                case "[":
                    sound = new Audio(G2); keyboadClass = "G2";
                    break
                case "Â¥":
                    sound = new Audio(Ab2); keyboadClass = "Ab2";
                    break
                case "z":
                    sound = new Audio(A2); keyboadClass = "A2";
                    break
                case "s":
                    sound = new Audio(Bb2); keyboadClass = "Bb2";
                    break
                case "x":
                    sound = new Audio(B2); keyboadClass = "B2";
                    break
                case "c":
                    sound = new Audio(C3); keyboadClass = "C3";
                    break
                case "f":
                    sound = new Audio(Db3); keyboadClass = "Db3";
                    break
                case "v":
                    sound = new Audio(D3); keyboadClass = "D3";
                    break
                case "g":
                    sound = new Audio(Eb3); keyboadClass = "Eb3";
                    break
                case "b":
                    sound = new Audio(E3); keyboadClass = "E3";
                    break
                case "n":
                    sound = new Audio(F3); keyboadClass = "F3";
                    break
                case "j":
                    sound = new Audio(Gb3); keyboadClass = "Gb3";
                    break
                case "m":
                    sound = new Audio(G3); keyboadClass = "G3";
                    break
                case "k":
                    sound = new Audio(Ab3); keyboadClass = "Ab3";
                    break
                case ",":
                    sound = new Audio(A3); keyboadClass = "A3";
                    break
                case "l":
                    sound = new Audio(Bb3); keyboadClass = "Bb3";
                    break
                case ".":
                    sound = new Audio(B3); keyboadClass = "B3";
                    break
                case "/":
                    sound = new Audio(C4); keyboadClass = "C4";
                    break
                default:
                    break;
            }
            if (sound === null) return;
            sound.play();
            setKeyboadColer({ bgColor: "#990000", bgClassName: keyboadClass });
            resetColor();
        }
        useEffect(() => {
            document.addEventListener('keydown', handleKeyDown, false)
        }, [])
    };

    const resetColor = useCallback(() => {
        setTimeout(() => {
            setKeyboadColer({ bgColor: "#FFF", bgClassName: "" });
        }, 100);
    }, [keyboadColer]);

    return (
        <Box className="Piano">
            <Box className="Piano-white">
                {whiteKeyList.map((item) => (
                    <Button
                        className={item}
                        variant="contained"
                        sx={{
                            backgroundColor: item === keyboadColer.bgClassName ? keyboadColer.bgColor : "#FFF",
                            border: "1px solid #000",
                            "&:hover": { opacity: 1 },
                        }}
                        color="error"
                    />
                ))}
            </Box>
            <Box className="Piano-black">
                {blackKeyList.map((item) => (
                    <Button
                        className={item.blackKey}
                        variant="contained"
                        sx={{ backgroundColor: item.blackKey === keyboadColer.bgClassName ? keyboadColer.bgColor : "#000", border: "1px solid #000", margin: item.keyPosition, "&:hover": { opacity: 1 } }}
                        color="error"
                    ></Button>
                ))}
            </Box>
        </Box>
    );
}

export const Main = () => {
    return (
        <Box className="Main" sx={sx}>
            <Sample></Sample>
        </Box>
    );
};

const sx: SxProps<Theme> = {
    "&.Main": {
        width: "100%",
    },
    ".Piano": {
        width: "1333px",
        height: "300px",
        margin: "10% 1% 0"
    },
    ".Piano-white": {
        position: "absolute",
        widht: "100%",
        height: "400px",
        display: "flex",
    },
    ".Piano-black": {
        position: "relative",
        width: "100%",
        height: "95%",
        display: "flex",
    },
};
