export const Main = () => {
    return (
        <Box className="HeMainader" sx={sx}>
            <Button variant="contained" onClick={() => {
                var base64 = '～ BASE64 エンコードされた wav データ ～';
                var sound = new Audio("data:audio/wav;base64," + base64);
                sound.play();
            }} >
                ボタンに表示するテキスト
            </Button>
        </Box>
    );
};

const sx: SxProps<Theme> = {
    "&.Main": {
        width: "100%",
    },
};
